
jQuery(document).ready(function(){jQuery(".flag-this").bind("click",function(){var thisButton=jQuery(this);var the_post_id=jQuery(this).attr('post-id');var the_slug=jQuery(this).attr('flag');jQuery.post(the_ajax_script.ajaxurl,{action:'flag',post_id:the_post_id,flag_slug:the_slug},function(data){thisButton.removeClass('flag-this');thisButton.addClass('flagged');thisButton.html(data);});});});